var tConn = require('./lib/connection.js')
var options = {host: 'localhost', port: '3301', timeout: 0}
conn = new tConn(options);

conn.connect()
.then(function(){
	return conn.upsert('counter', [['+', 1, 1]], ['test', 1])
})
.then(function(){
	return conn.select('counter', 'primary', 1, 0, 'eq', ['test']);
})
.then(console.log)
.catch(console.error)
